package com.clapi.data;

import java.util.List;

/**
 * Assert objects of the system. This file can be generated by the system or the user.
 * 
 * @author ericpinet
 * <br> 2016-07-08
 */
public class Assert implements DataObj {

	/**
	 * Enum AssertType
	 * 
	 * @author ericpinet
	 * <br> 2016-07-08
	 */
	public enum AssertType {
		IMAGE,
		FILE,
		OTHER	
	}
	
	/**
	 * Enum AssertMode
	 * 
	 * @author ericpinet
	 * <br> 2016-07-08
	 */
	public enum AssertMode {
		SYSTEM,
		USER	
	}
	
	/**
	 * UID of the assert generated by the server.
	 */
	private String uid;
	
	/**
	 * label of the assert.
	 */
	private String label;
	
	/** 
	 * Type of the assert.
	 */
	private AssertType type;
	
	/** 
	 * Mode of the assert.
	 */
	private AssertMode mode;
	
	/**
	 * Default constructor. 
	 * 
	 * @param uid Uid of the assert.
	 * @param label Label of the assert.
	 * @param type Type of the assert.
	 * @param mode Mode of the assert.
	 */
	public Assert(String uid, String label, AssertType type, AssertMode mode) {
		super();
		this.uid = uid;
		this.label = label;
		this.type = type;
		this.mode = mode;
	}

	/**
	 * @return the uid
	 */
	public String getUid() {
		return uid;
	}

	/**
	 * @param uid the uid to set
	 */
	public void setUid(String uid) {
		this.uid = uid;
	}

	/**
	 * @return the label
	 */
	public String getLabel() {
		return label;
	}

	/**
	 * @param label the label to set
	 */
	public void setLabel(String label) {
		this.label = label;
	}

	/**
	 * @return the type
	 */
	public AssertType getType() {
		return type;
	}

	/**
	 * @param type the type to set
	 */
	public void setType(AssertType type) {
		this.type = type;
	}

	/**
	 * @return the mode
	 */
	public AssertMode getMode() {
		return mode;
	}

	/**
	 * @param mode the mode to set
	 */
	public void setMode(AssertMode mode) {
		this.mode = mode;
	}

	/**
	 * Return children of this object. 
	 * 
	 * @return Children of this object.
	 * @see com.clapi.data.DataObj#getChildren()
	 */
	@Override
	public List<DataObj> getChildren() {
		return null;
	}

}
