package com.clapi.data;

import java.util.List;
import java.util.Vector;

/**
 * Home.
 * 
 * @author ericpinet
 * <br> 2015-11-08
 */
public class Home extends DataObjDefault implements DataObj {
	
	/**
	 * UID of the home generated by the server.
	 */
	private String uid;
	
	/**
	 * Label of the home.
	 */
	private String label;
	
	/**
	 * List of zones in the home.
	 */
	private List<Zone> zones;
	
	/**
	 * Image uid of the home.
	 */
	private String imageuid;

	/**
	 * Default constructor. 
	 * 
	 * @param uid Uid of the home.
	 * @param label Label of the home.
	 * @param zones Zones of the home.
	 * @param imageuid ImageUID of the home.
	 */
	public Home(String uid, String label, List<Zone> zones, String imageuid) {
		super();
		this.uid = uid;
		this.label = label;
		this.zones = zones;
		this.imageuid = imageuid;
	}
	
	/**
	 * Default constructor. 
	 * 
	 * @param uid Uid of the home.
	 * @param label Label of the home.
	 */
	public Home(String uid, String label) {
		super();
		this.uid = uid;
		this.label = label;
		this.zones = new Vector<Zone>();
		this.imageuid = "";
	}

	/**
	 * @return the uid
	 */
	public String getUid() {
		return uid;
	}

	/**
	 * @param uid the uid to set
	 */
	public void setUid(String uid) {
		this.uid = uid;
	}

	/**
	 * @return the label
	 */
	public String getLabel() {
		return label;
	}

	/**
	 * @param label the label to set
	 */
	public void setLabel(String label) {
		this.label = label;
	}

	/**
	 * @return the zones
	 */
	public List<Zone> getZones() {
		return zones;
	}

	/**
	 * @param zones the zones to set
	 */
	public void setZones(List<Zone> zones) {
		this.zones = zones;
	}
	
	/**
	 * @param zone the zone to add
	 */
	public void addZone(Zone zone) {
		this.zones.add(zone);
	}

	/**
	 * @return the imageuid
	 */
	public String getImageuid() {
		return imageuid;
	}

	/**
	 * @param imageuid the imageuid to set
	 */
	public void setImageuid(String imageuid) {
		this.imageuid = imageuid;
	}
	
	/**
	 * Return the children of this object.
	 * 
	 * @return Children of this object.
	 * @see com.clapi.data.DataObj#getChildren()
	 */
	@SuppressWarnings("unchecked")
	@Override
	public List<DataObj> getChildren() {
		return (List<DataObj>)(Object)getZones();
	}
}