package com.clapi.data;

import java.util.List;

/**
 * Service for an accessory.
 * 
 * @author ericpinet
 * <br> 2015-11-08
 */
public class Service {
	
	/**
	 * UID of the service generated by the server.
	 */
	private String uid;
	
	/**
	 * Name of the service.
	 */
	private String name;
	
	/**
	 * List of characteristics in the accessory service.
	 */
	private List<Characteristic> characteristics;

	/**
	 * Default constructor. 
	 * 
	 * @param uid
	 * @param name
	 * @param characteristics
	 */
	public Service(String uid, String name, List<Characteristic> characteristics) {
		super();
		this.uid = uid;
		this.name = name;
		this.characteristics = characteristics;
	}
	
	/**
	 * Default constructor. 
	 * 
	 * @param uid
	 */
	public Service(String uid, String name) {
		super();
		this.uid = uid;
		this.name = name;
		
	}

	/**
	 * @return the uid
	 */
	public String getUid() {
		return uid;
	}

	/**
	 * @param uid the uid to set
	 */
	public void setUid(String uid) {
		this.uid = uid;
	}
	
	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @param uid the name to set
	 */
	public void setName(String name) {
		this.name = name;
	}

	/**
	 * @return the characteristics
	 */
	public List<Characteristic> getCharacteristics() {
		return characteristics;
	}

	/**
	 * @param characteristics the characteristics to set
	 */
	public void setCharacteristics(List<Characteristic> characteristics) {
		this.characteristics = characteristics;
	}
}
